import {useState , useEffect} from "react";
import './App.css';

function App() {
  const [password, setPassword] = useState('');
  const [isNum, setNum] = useState('');
  const [isSpeical, setSpeical] = useState('');
  const [passLength, setpassLength] = useState(8);

   const genratePassword = () => {
        const str = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz";
        const number = "1234567890";
        const special = "!@#$%^&*()_+~";
        let newPassword = "";

        const allChars = str + (isNum ? number : '') + (isSpeical ? special : '');

        for (let i = 0; i < passLength; i++) {
            newPassword += allChars.charAt(Math.floor(Math.random() * allChars.length));
        }

        console.log("Generated password:", newPassword);
        setPassword(newPassword);
    };


  useEffect(() => {
  genratePassword();

}, [isNum, isSpeical, passLength]);


const handleNumChange = (e) => {
        setNum(e.target.checked);
    };

const handleSpecialChange = (e) => {
        setSpeical(e.target.checked);
    };

const handleRangeChange = (e) => {
     setpassLength(parseInt(e.target.value));
};

  return (
    <>
            <h1 className="text-center">Password Generator</h1>
            <div>
                <input type="text" value={password} readOnly className="password-input" />
                <button className="copy-button">Copy</button>
            </div>
            <div className="options">
                <label>
                    <input type="checkbox" checked={isNum} onChange={handleNumChange} />
                    Number
                </label>
                <label>
                    <input type="checkbox" checked={isSpeical} onChange={handleSpecialChange} />
                    Special
                </label> 
                <label>
                   <input type="range" min="1" max="100" value={passLength} 
                   onChange={handleRangeChange}class="slider" /> 
                    Range
                </label>
            </div>
        </>
  );
}

export default App;
